package org.example.bot_demo;

import com.mikuac.shiro.core.Bot;
import com.mikuac.shiro.core.BotPlugin;
import com.mikuac.shiro.dto.event.message.GroupMessageEvent;
import com.mikuac.shiro.dto.event.message.PrivateMessageEvent;
import org.example.bot_demo.service.MessageService;
import org.springframework.stereotype.Component;

import java.io.IOException;

@Component
public class TestPlugin extends BotPlugin {


    @Override
    public int onPrivateMessage(Bot bot, PrivateMessageEvent event) {
        if ("hello".equals(event.getMessage())) {
            try {
                MessageService.remotePrivate(bot,event,"测试消息","data:image/webp;base64,");
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        }
        return MESSAGE_IGNORE;
    }

    @Override
    public int onGroupMessage(Bot bot, GroupMessageEvent event) {
        if ("喵喵喵".equals(event.getMessage())) {
            try {
                MessageService.localGroupRandom(bot,event,"\n随机猫猫：\n");
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        }else if("KeepCoding".equals(event.getMessage())){
            try {
                MessageService.localGroup(bot,event,"","/Pic/2.jpg");
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        }else if("晚安喵".equals(event.getMessage())){
            try {
                MessageService.localGroup(bot,event,"\n辛苦了喵，おやすみ\n","/Pic/11.jpg");
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        }

        return MESSAGE_IGNORE;
    }

}